import { ClassesService } from '../../services/classes.service';
import { ExamService } from '../../services/exam.service';
import { TeacherService } from '../../services/teacher.service';
import { StudentService } from '../../services/student.service';
// Import Models
import { Classes } from '../../domain/shri-dev-pathshal-a_db/classes';
import { Exam } from '../../domain/shri-dev-pathshal-a_db/exam';
import { Student } from '../../domain/shri-dev-pathshal-a_db/student';
import { Teacher } from '../../domain/shri-dev-pathshal-a_db/teacher';

