/**
 * This component allows to edit a Classes
 */
@Component({
    selector: 'app-classes-edit',
    templateUrl: 'classes-edit.component.html',
    styleUrls: ['classes-edit.component.css']
})
export class ClassesEditComponent implements OnInit {
    item: Classes;
    externalExam__Class: Exam[];
    externalStudent__Classes: Student[];
    externalTeacher__Classes: Teacher[];
    model: Classes;
    formValid: Boolean;

    constructor(
    private classesService: ClassesService,
    private examService: ExamService,
    private teacherService: TeacherService,
    private studentService: StudentService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Classes();
        this.externalExam__Class = [];
        this.externalStudent__Classes = [];
        this.externalTeacher__Classes = [];
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.classesService.get(id).subscribe(item => this.item = item);
                this.examService.findBy_Class(id).subscribe(list => this.externalExam__Class = list);
                this.studentService.findBy_Classes(id).subscribe(list => this.externalStudent__Classes = list);
                this.teacherService.findBy_Classes(id).subscribe(list => this.externalTeacher__Classes = list);
            }
            // Get relations
        });
    }


    /**
     * Save Classes
     *
     * @param {boolean} formValid Form validity check
     * @param Classes item Classes to save
     */
    save(formValid: boolean, item: Classes): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.classesService.update(item).subscribe(data => this.goBack());
            } else {
                this.classesService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



